import unittest
from typing import TypedDict, List


class Item(TypedDict):
    """
    Represents a single product entry in the order.

    :key name: The name of the product.
    :key price: The price per unit of the product.
    :key quantity: The number of units of the product.
    """

# Dummy OrderCalculator class for testing purposes, as the actual implementation is not provided.
# In a real scenario, this class would be imported from its module.
class OrderCalculator:
    def __init__(self, tax_rate=0.23, free_shipping_threshold=100.0, shipping_cost=10.0):
        if not (0.0 <= tax_rate <= 1.0):
            raise ValueError("Tax rate must be between 0.0 and 1.0.")
        if free_shipping_threshold < 0.0:
            raise ValueError("Free shipping threshold cannot be negative.")
        if shipping_cost < 0.0:
            raise ValueError("Shipping cost cannot be negative.")
        if not isinstance(tax_rate, (int, float)) or \
           not isinstance(free_shipping_threshold, (int, float)) or \
           not isinstance(shipping_cost, (int, float)):
            raise TypeError("Tax rate, free shipping threshold, and shipping cost must be numbers.")

        self.tax_rate = tax_rate
        self.free_shipping_threshold = free_shipping_threshold
        self.shipping_cost = shipping_cost
        self._items: List[Item] = []

    def add_item(self, name: str, price: float, quantity: int = 1):
        if not isinstance(name, str) or not isinstance(price, (int, float)) or not isinstance(quantity, int):
            raise TypeError("Name must be string, price must be number, quantity must be integer.")
        if not name:
            raise ValueError("Item name cannot be empty.")
        if price <= 0:
            raise ValueError("Price must be greater than 0.")
        if quantity < 1:
            raise ValueError("Quantity must be at least 1.")

        for item in self._items:
            if item['name'] == name:
                if item['price'] == price:
                    item['quantity'] += quantity
                    return
                else:
                    raise ValueError(f"Item '{name}' already exists with a different price.")
        self._items.append(Item(name=name, price=price, quantity=quantity))

    def remove_item(self, name: str):
        if not isinstance(name, str):
            raise TypeError("Name must be a string.")
        initial_len = len(self._items)
        self._items = [item for item in self._items if item['name'] != name]
        if len(self._items) == initial_len:
            raise ValueError(f"No item with name '{name}' found in the order.")

    def get_subtotal(self) -> float:
        if not self._items:
            raise ValueError("Order is empty, cannot calculate subtotal.")
        return sum(item['price'] * item['quantity'] for item in self._items)

    def apply_discount(self, subtotal: float, discount: float) -> float:
        if not isinstance(subtotal, (int, float)) or not isinstance(discount, (int, float)):
            raise TypeError("Subtotal and discount must be numbers.")
        if subtotal < 0:
            raise ValueError("Subtotal cannot be negative.")
        if not (0.0 <= discount <= 1.0):
            raise ValueError("Discount must be between 0.0 and 1.0.")
        return subtotal * (1 - discount)

    def calculate_shipping(self, discounted_subtotal: float) -> float:
        if not isinstance(discounted_subtotal, (int, float)):
            raise TypeError("Discounted subtotal must be a number.")
        if discounted_subtotal < self.free_shipping_threshold:
            return self.shipping_cost
        return 0.0

    def calculate_tax(self, amount: float) -> float:
        if not isinstance(amount, (int, float)):
            raise TypeError("Amount must be a number.")
        if amount < 0:
            raise ValueError("Amount cannot be negative.")
        return amount * self.tax_rate

    def calculate_total(self, discount: float = 0.0) -> float:
        if not isinstance(discount, (int, float)):
            raise TypeError("Discount must be a number.")
        if not (0.0 <= discount <= 1.0):
            raise ValueError("Discount must be between 0.0 and 1.0.")
        if not self._items:
            raise ValueError("Order is empty, cannot calculate total.")

        subtotal = self.get_subtotal()
        discounted_subtotal = self.apply_discount(subtotal, discount)
        shipping_cost = self.calculate_shipping(discounted_subtotal)
        tax_amount = self.calculate_tax(discounted_subtotal + shipping_cost)
        return discounted_subtotal + shipping_cost + tax_amount

    def total_items(self) -> int:
        return sum(item['quantity'] for item in self._items)

    def clear_order(self):
        self._items = []

    def list_items(self) -> List[str]:
        return sorted(list(set(item['name'] for item in self._items)))

    def is_empty(self) -> bool:
        return not bool(self._items)


